#include <iostream>
#include <vector>
#include <algorithm>
#include <math.h>
using namespace std;

const int MAX_N = 1000;

string lastSubstring(string s)
{
    char a = 'a';
    for (int i = 0; i < s.size(); i++)
    {
        if (s[i] > a)
        {
            a = s[i];
        }
    }
    vector<int> vc;
    for (size_t i = 0; i < s.size(); i++)
    {
        if (s[i] == a)
            vc.push_back(i);
    }
    vector<int> vd = vc;
    int length = vc.size();
    while (true)
    {

        char a = 'a';
        for (int i = 0; i < vd.size(); i++)
        {
            if(vd[i]==99999){
                continue;
            }
            if (vd[i] + 1 < s.size())
            {
                if (s[vd[i] + 1] > a)
                {
                    a = s[vd[i] + 1];
                }
            }
        }

        for (int i = 0; i < vc.size(); i++)
        {
            if (vc[i] + 1 < s.size())
            {
                if (s[vc[i] + 1] == a)
                {
                    vd[i] = -(vd[i] + 1);
                    vc[i] = -vc[i];
                }
            }
        }

        vector<int>::iterator it = vd.begin();
        while (it < vd.end())
        {
            if (*it > 0 && *it != 999999)
            {
                *it = 999999;
                
            }
            else
            {
                *it = -*it;
            }
            it++;
        }
        it = vc.begin();
        while (it < vc.end())
        {
            if (*it > 0 && *it != 999999)
            {
                *it = 999999;
                length--;
            }
            else
            {
                *it = -*it;
            }
            it++;
        }
        if (length == 1)
        {
            break;
        }
    }
    int i = 0 ;
    for(; i < vc.size() ; i++){
        if(vc[i]!=999999)
         break;
    }
    return s.substr(abs(vc[i]));
}
int main()
{
    string a = "";
    string b = "bab";
    string m = lastSubstring(a);

}